package cn.wlk.keepcoding.leetcode;

import org.junit.Test;

/**
 * 387. 字符串中的第一个唯一字符
 * <p>
 * 给定一个字符串，找到它的第一个不重复的字符，并返回它的索引。如果不存在，则返回 -1。
 * <p>
 * 案例:
 * <p>
 * s = "leetcode"
 * 返回 0.
 * <p>
 * s = "loveleetcode",
 * 返回 2.
 * <p>
 * <p>
 * 注意事项：您可以假定该字符串只包含小写字母。
 */
//done
public class n387 {
    public int firstUniqChar(String s) {
        char[] ch = s.toCharArray();
        int len = ch.length;
        int[] set = new int[265];
        for (int i = 0; i < 265; i++) {
            set[i] = -1;
        }
        for (int i = 0; i < len; i++) {
            if (set[ch[i]] == -1)
                set[ch[i]] = i;
            else set[ch[i]] = -2;
        }
        int min = Integer.MAX_VALUE;
        for (int i = 'a'; i <= 'z'; i++) {
            if (set[i] >= 0)
                if (set[i] < min) {
                    min = set[i];
                }
        }
        return min== Integer.MAX_VALUE?-1:min;


    }

    @Test
    public void test() {
        n387 t = new n387();
        System.out.println(t.firstUniqChar("hheekki"));
        System.out.println(t.firstUniqChar("hheekkddd"));

        System.out.println(t.firstUniqChar(""));
    }

}
